import React, { useEffect, useState } from "react";
import Question from './Question'
import QuestionCategorys from './QuestionCategorys'
import QuestionDifficulty from './QuestionDifficulty'
import axios from "axios";


const Questionnaire = () => {
  // const getQuestionare = () => {

  // }
  // const getDifficulty = () => {

  // }

  const getCategory = (index) => {
    let newCategories = [...categories]
    console.log(newCategories[index])
  }
  
  const apiurl = "https://the-trivia-api.com/api/categories";
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  const getCaregories = async () => {
    const res = await axios.get(apiurl);
    setCategories(Object.keys(res.data));
    setLoading(false);
  };
  useEffect(() => {
    getCaregories();
  }, []);

  return (
    <section>
      <header>
        <h1>Fragebogen-Erstellen</h1>
      </header>
      <div>
        {loading ? (
          <h4>Wait for Api...</h4>) :
              (<QuestionCategorys
                setCategorie={getCategory}
                categories={categories}
              ></QuestionCategorys>
            )
          }

        <QuestionDifficulty />
      </div>
    </section>

  )
}

export default Questionnaire
