import React from 'react'
import Question from './Question'
import { useState } from 'react'

const RandomQuizList = ({ allQuestions }) => {
    const [currentQuestion, setCurrentQuestion] = useState(0)

    const checkAnswer = (event) => {

        if (currentQuestion < allQuestions.length) {
            // document.getElementById("actualQuestionPosition").innerHTML = parseInt(questionPosition) + 1;
            setCurrentQuestion(currentQuestion + 1)
        }

        if (event.currentTarget.getAttribute("correct") === "yes") {
            event.currentTarget.style.backgroundColor = "green";
            event.currentTarget.style.color = "white";

        } else {
            event.currentTarget.style.backgroundColor = "red";
            event.currentTarget.style.color = "white";
        }

    };

    return (
        <section className='quizList'>
            <div>Question <span id="actualQuestionPosition">1</span> of {allQuestions.length} </div>
            {allQuestions.map((item, index) => {
             if (index === parseInt(document.getElementById("actualQuestionPosition").innerHTML) - 1) {
                return (
                    <Question
                        key={index}
                        questionName={allQuestions[0].question}
                        correctAnswer={allQuestions[0].correctAnswer}
                        incorrectAnswers={allQuestions[0].incorrectAnswers}
                        checkAnswer={checkAnswer}
                    ></Question>
                )
            }
            })}
        </section>
    )
}
export default RandomQuizList
