import React from 'react'

const Question = ({ questionName, correctAnswer, incorrectAnswers }) => {
  let answerPossibilities = incorrectAnswers.map((item,index) => {
      var arr = [];
      arr[index] = {answer : item, correct : false} 
  });



  answerPossibilities.push({answer : correctAnswer, correct : true} );

  const shuffle = (a) => {
    for (let i = a.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [a[i], a[j]] = [a[j], a[i]];
    }
    return a;
}
shuffle(answerPossibilities);


  return (
    <article className='oneQuestion'>
      <header>
        <h2>{questionName}</h2>
      </header>

      <div className='answerPossibilities'>
        <div></div>

        {incorrectAnswers.map((item) => {
          return (
            <div correct="false">{item}</div>
          )
        })}

      </div>
    </article>
  )
}
export default Question
