import React from 'react'

const Question = ({ questionName, correctAnswer, incorrectAnswers }) => {
  let answerPossibilities = incorrectAnswers.map((item,index) => {
      var arr = [];
      arr.push({answer: item, correct: false})

      return arr;
  });



  answerPossibilities.push({answer : correctAnswer, correct : true} );

  const shuffle = (a) => {
    for (let i = a.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [a[i], a[j]] = [a[j], a[i]];
    }
    return a;
}

// shuffle(answerPossibilities);
console.log(answerPossibilities)

  return (
    <article className='oneQuestion'>
      <header>
        <h2>{questionName}</h2>
      </header>

      <div className='answerPossibilities'>
        <div></div>

        {answerPossibilities.map((item) => {
          console.log(item.answer)
          return (
            <div correct={item.correct}>{item.answer}</div>
          )
        })}

      </div>
    </article>
  )
}
export default Question
