import React from 'react'

const InputField = ({ inputLabel, action, buttonLabel, onlyButton , onlyInput }) => {

    return (
        <section className="formWrapper">
            {onlyButton === true ?
                (
                    <form>
                        <button onClick={action}>buttonLabel</button>
                    </form>
                ) : onlyInput === true (
                    <form>
                        <label>
                            {inputLabel}
                        </label>
                        <input id="setUsername" type="text"></input>
                        <button onClick={action}>{buttonLabel}</button>
                    </form>
                )
            }
        </section>

    )
}
export default InputField;
