import axios from 'axios'
import InputField from './InputField';

const CountryQuiz = () => {
    const apiUrl = "https://restcountries.com/v3.1/all";
    const [setCountryData, countryData] = useState([]);

    const callApi = async () => {
        const res = await axios.get(apiUrl);
        formatApiCountryData(res.data);
    }
    const formatApiCountryData = (apiData) => {
        let filteredData = {};
        apiData.forEach((item) => {
            filteredData[item.translations.deu.common] = {
            "population" : item.population,
            "capital" : item.capital?.[0],
            "continent" : item.contintens?.[0],
            "area" : item.area}

        })
        setCountryData(filteredData);
    }
    
    const getRandomCountry = (countryData) => {
        console.log();

        var keys = countryData.keys();
        return countryData[keys[ keys.length * Math.random() << 0]];
    }
