import React, { useEffect, useState } from "react";
import Question from './Question'
import QuestionCategorys from './QuestionCategorys'
import QuestionDifficultys from './QuestionDifficultys'
import axios from "axios";


const Questionnaire = () => {
  const allDifficultys = [
    { active : false, difficulty : 'Easy'},
    { active : false, difficulty : 'Medium'},
    { active : false, difficulty : 'Hard'}
  ];

  const getQuizConfig = () => {
    let quizConfig = [];

    quizConfig.push({ difficulty: localStorage.getItem("quizDifficulty") });
    quizConfig.push({ categorie: localStorage.getItem("quizCategory") });

    console.log(quizConfig);
  }

  const getCategory = (index) => {
    let quizCategorys = [...categories]
    localStorage.setItem('quizCategory', quizCategorys[index])
  }
  const getDifficulty = (index) => {
    allDifficultys[index].active = true;
    localStorage.setItem('quizDifficulty', allDifficultys[index])
    getQuizConfig();
  }



  const apiurl = "https://the-trivia-api.com/api/categories";
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  const getCaregories = async () => {
    const res = await axios.get(apiurl);
    setCategories(Object.keys(res.data));
    setLoading(false);
  };
  useEffect(() => {
    getCaregories();
  }, []);

  return (
    <section>
      <header>
        <h1>Fragebogen-Erstellen</h1>
      </header>
      {loading ? (
        <h4>Wait for Api...</h4>) :
        (
          <React.Fragment>
            <QuestionCategorys
              setCategorie={getCategory}
              categories={categories}
            ></QuestionCategorys>

            <QuestionDifficultys
              setDifficulty={getDifficulty}
              difficultys={allDifficultys}
            ></QuestionDifficultys>
          </React.Fragment>
        )
      }

    </section>

  )
}

export default Questionnaire
