import logo from './logo.svg';
import './App.css';
import RandomQuizGenerator from './components/RandomQuizGenerator';
import Questionairies from './components/Questionairies';


function App() {
  const startQuiz = element => {
    let elementToShow = document.getElementById("show" + element.currentTarget.id);

    elementToShow.className ? "hide" (elementToShow.className = "show") : elementToShow.className="hide"


  }

  return (
    <section className="App">
      <header className="App-header">
        <h1>Quiz-App</h1>
      </header>
      <section id="RandomQuiz" onClick={startQuiz}>
        <header><h2>Random-Quiz</h2></header>
      </section>

      <section id="showRandomQuiz" className="hide">
        <RandomQuizGenerator />
      </section>

      <section id="Questionairies" onClick={startQuiz}>
        <header><h2>Fragebögen</h2></header>
      </section>

      <section id="showQuestionairies" className="hide">
        <Questionairies />
      </section>

    </section>
  );
}

export default App;
