import React from 'react'

const Country = ({ countryToGuess, name, population, continent, area }) => {
    //check properties
    var arrowClass = "arrow ";
    var arrowClassPopulation;
    var arrowClassArea;
    
    //Population
    countryToGuess.population < population ? arrowClassPopulation = arrowClass + "up" : arrowClassPopulation = arrowClass + "down";

    countryToGuess.area < area ? arrowClassArea = arrowClass + "up" : arrowClassArea = arrowClass + "down";



    //check Area
    if(countryToGuess.area < area) {
        arrowClassArea = arrowClass + "down";
    } else if (countryToGuess.area > area) {
        arrowClassArea = arrowClass + "up";

    } else {
        arrowClassArea = "";
    }

    //check continent

    return (

        <div className='oneCountry'>
            <span className='countryItem itemName'>{name}</span><br/>
            <span className='countryItem' id ='itemPopulation'>Einwohnerzahl: {population}<span><i className={arrowClassPopulation}></i></span></span>
            <span className='countryItem' id ='itemContinent'>Kontinent: {continent}</span>
            <span className='countryItem' id ='itemArea'>Flächengröße: {area}<span><i className={arrowClassArea}></i></span></span>
        </div>
    )
}
export default Country;
