import React from 'react'

const Country = ({ countryToGuess, name, population, continent, area }) => {
    //check properties
    var arrowClass = "arrow hide";
    
    if(countryToGuess.population < population) {
        arrowClass = arrowClass + "down";

    } else if (countryToGuess.population > population) {
        arrowClass = arrowClass + "up";

    } else {

    }

    //check continent
    //check Area

    return (

        <div className='oneCountry'>
            <span className='countryItem itemName'>{name}</span><br/>
            <span className='countryItem' id ='itemPopulation'>Einwohnerzahl: {population}<span><i className={arrowClass}></i></span></span>
            <span className='countryItem' id ='itemContinent'>Kontinent: {continent}</span>
            <span className='countryItem' id ='itemArea'>Flächengröße: {area}<span><i className={arrowClass}></i></span></span>
        </div>
    )
}
export default Country;
