    const [currentQuestion, setCurrentQuestion] = useState(0);
    let allQuestions = [];

    const setAllQuestions = () => {

        allQuestions.push(easyQuestions);
        allQuestions.push(mediumQuestions);
        allQuestions.push(hardQuestions);
    }
    setAllQuestions();

    const checkAnswer = (event) => {
        var answerPossibilities = document.getElementsByClassName(event.currentTarget.className);

            for (var answerPossibilitie of answerPossibilities) {
