import React, {useEffect, useState} from 'react'
import axios from 'axios'
import InputField from './InputField';

const CountryQuiz = () => {
    const apiUrl = "https://restcountries.com/v3.1/all";
    const [quizState , setQuizState] = useState("generate");
    const [countryToGuess , setCountry] = useState({});


    const callApi = async () => {
        const res = await axios.get(apiUrl);
        formatApiCountryData(res.data);
    }
    const formatApiCountryData = (apiData) => {
        let filteredData = {};
        apiData.forEach((item) => {
            filteredData[item.translations.deu.common] = {
            "population" : item.population,
            "capital" : item.capital?.[0],
            "continent" : item.continents?.[0],
            "area" : item.area}
        })
     
        var keys = Object.keys(filteredData);
        setCountry(filteredData[keys[ keys.length * Math.random() << 0]]);
    }
    const checkCountry = (e) => {
       e.preventDefault();

    }
    const startQuiz = () => {
        callApi();
    }


   useEffect(() => {
    startQuiz();
    }, []);
    
    return (
        console.log(countryToGuess),
        <section className="countryQuiz">
            <header>
                <h1>Land in 5 versuchen erraten</h1>
                </header>


            <InputField
            checkInput={checkCountry}
            label={"Land eingeben und loslegen"}
            ></InputField>

        </section>
    )
}
export default CountryQuiz;
