import React from 'react'

const QuizScore = ({ quizConfig, setQuizState, isMillionär }) => {
    return (
        <section className="quizScore">
            <header>
                <h1>Auswertung von {localStorage.getItem("Username")}</h1>

                {isMillionär ?
                
                sessionStorage.getItem("Score") > 0 
                     ? (<h2> Sie sind nach der {sessionStorage.getItem("Score") + 1} Frage gescheitert </h2>)
                     : (<h2> Sie sind an der 1 Frage gescheietert. </h2>)
                    
                    : (<h2>{sessionStorage.getItem("Score")} von {quizConfig.count} Fragen richtig</h2>)
                }

            </header>
            <p onClick={() => setQuizState("config")} className=' button'>Zurück zur Anfang </p>

        </section>
    )
}
export default QuizScore;
