import React from 'react'

const Question = ({ questionName, correctAnswer, incorrectAnswers }) => {
  let answerPossibilities = incorrectAnswers;
  answerPossibilities.push(correctAnswer);

  return (
    <article className='oneQuestion'>
      <header>
        <h2>{questionName}</h2>
      </header>

      <div className='answerPossibilities'>
        <div></div>

        {incorrectAnswers.map((item) => {
          return (
            <div onClick="{}" correct="false">{item}</div>
          )
        })}

      </div>
    </article>
  )
}
export default Question
