import React from 'react'

const Question = ({ questionName, correctAnswer, incorrectAnswers, allQuestionsCount }) => {
  let answerPossibilities = [];
  var questionStartPosition = parseInt(document.getElementById("actualQuestionPosition").innerHTML);


  incorrectAnswers.forEach((item, index) => {
    answerPossibilities.push({ "answer": item, "correct": false })
  });
  answerPossibilities.push({ "answer": correctAnswer, "correct": true });

  const shuffle = (a) => {
    for (let i = a.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [a[i], a[j]] = [a[j], a[i]];
    }
    return a;
  }
  const checkAnswer = (event) => {
    var questionPosition = document.getElementById("actualQuestionPosition").innerHTML;
    
    if(questionPosition < allQuestionsCount) {
      document.getElementById("actualQuestionPosition").innerHTML = parseInt(questionPosition) + 1;
    }

    if (event.currentTarget.getAttribute("correct") === "yes") {
      event.currentTarget.style.backgroundColor = "green";
      event.currentTarget.style.color = "white";

    } else {
      event.currentTarget.style.backgroundColor = "red";
      event.currentTarget.style.color = "white";
    }
  };

  shuffle(answerPossibilities);

  return (
    <section>
      <div>Question <span id="actualQuestionPosition">1</span> of {allQuestionsCount} </div>
      <article className='oneQuestion'>
        <header>
          <h2>{questionName}</h2>
        </header>

        <div className='answerPossibilities'>
          {answerPossibilities[questionStartPosition].map((item, index) => {
            return (
              <div onClick={checkAnswer} key={index} className='answerPossibilitie' correct={item.correct ? "yes" : "no"}>{item.answer}</div>
            )
          })}

        </div>
      </article>
    </section>
  )
}
export default Question
