import React from 'react'

const Question = ({ questionName, correctAnswer, incorrectAnswers, questionIndex, checkAnswer , changeQuestion, quizScore}) => {
  let answerPossibilities = [];

  incorrectAnswers.forEach((item, index) => {
    answerPossibilities.push({ "answer": item, "correct": false })
  });
  answerPossibilities.push({ "answer": correctAnswer, "correct": true });

  const shuffle = (a) => {
    for (let i = a.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [a[i], a[j]] = [a[j], a[i]];
    }
    return a;
  }

  if (!(quizScore < 0))  {
    shuffle(answerPossibilities);
  } 

  return (
    <section>
      <article className='oneQuestion'>
        <header>
          <h2>{questionName}</h2>
        </header>

        <div className='answerPossibilities'>
          {answerPossibilities.map((item, index) => {
            return (
              <div onClick={checkAnswer} key={index} questionindex={questionIndex} className='answerPossibilitie' correct={item.correct ? "yes" : "no"}>{item.answer}</div>
            )
          })}
        </div>
        <p onClick={changeQuestion} className='nextQuestion button'>Weiter</p>
      </article>
    </section>
  )
}
export default Question
